	public byte[] getExcelM_LARADVARCHIVAL(String filename, String reportId, String fromdate, String todate,
			String currency, String dtltype, String type, BigDecimal version) throws Exception {

		logger.info("Service: Starting Excel generation process in memory.");

		if (type.equals("ARCHIVAL") && version != null) {
			// Additional logic can go here if needed
		}

		// --- Fetch Data ---
		List<M_LARADV_Archival_Summary_Entity1> dataList = M_LARADV_Archival_Summary_Repo1
				.getdatabydateListarchival(dateformat.parse(todate), version);

		List<M_LARADV_Archival_Summary_Entity2> dataList1 = M_LARADV_Archival_Summary_Repo2
				.getdatabydateListarchival(dateformat.parse(todate), version);

		List<M_LARADV_Archival_Summary_Entity3> dataList2 = M_LARADV_Archival_Summary_Repo3
				.getdatabydateListarchival(dateformat.parse(todate), version);

		List<M_LARADV_Archival_Summary_Entity4> dataList3 = M_LARADV_Archival_Summary_Repo4
				.getdatabydateListarchival(dateformat.parse(todate), version);

		List<M_LARADV_Archival_Summary_Entity5> dataList4 = M_LARADV_Archival_Summary_Repo5
				.getdatabydateListarchival(dateformat.parse(todate), version);

		if (dataList.isEmpty() && dataList1.isEmpty() && dataList2.isEmpty() && dataList3.isEmpty()
				&& dataList4.isEmpty()) {
			logger.warn("Service: No data found for M_CA5 archival report. Returning empty result.");
			return new byte[0];
		}

		// --- Load Excel Template ---
		String templateDir = env.getProperty("output.exportpathtemp");
		String templateFileName = filename;
		Path templatePath = Paths.get(templateDir, templateFileName);

		logger.info("Service: Attempting to load template from path: {}", templatePath.toAbsolutePath());

		if (!Files.exists(templatePath)) {
			throw new FileNotFoundException("Template file not found at: " + templatePath.toAbsolutePath());
		}
		if (!Files.isReadable(templatePath)) {
			throw new SecurityException(
					"Template file exists but is not readable (check permissions): " + templatePath.toAbsolutePath());
		}

		// --- Create Workbook ---
		try (InputStream templateInputStream = Files.newInputStream(templatePath);
				Workbook workbook = WorkbookFactory.create(templateInputStream);
				ByteArrayOutputStream out = new ByteArrayOutputStream()) {

			Sheet sheet = workbook.getSheetAt(0);
			CreationHelper createHelper = workbook.getCreationHelper();

			// --- Style Definitions ---
			CellStyle dateStyle = workbook.createCellStyle();
			dateStyle.setDataFormat(createHelper.createDataFormat().getFormat("dd-MM-yyyy"));
			dateStyle.setBorderBottom(BorderStyle.THIN);
			dateStyle.setBorderTop(BorderStyle.THIN);
			dateStyle.setBorderLeft(BorderStyle.THIN);
			dateStyle.setBorderRight(BorderStyle.THIN);

			CellStyle textStyle = workbook.createCellStyle();
			textStyle.setBorderBottom(BorderStyle.THIN);
			textStyle.setBorderTop(BorderStyle.THIN);
			textStyle.setBorderLeft(BorderStyle.THIN);
			textStyle.setBorderRight(BorderStyle.THIN);

			Font font = workbook.createFont();
			font.setFontHeightInPoints((short) 8);
			font.setFontName("Arial");

			CellStyle numberStyle = workbook.createCellStyle();
			numberStyle.setBorderBottom(BorderStyle.THIN);
			numberStyle.setBorderTop(BorderStyle.THIN);
			numberStyle.setBorderLeft(BorderStyle.THIN);
			numberStyle.setBorderRight(BorderStyle.THIN);
			numberStyle.setFont(font);

			// --- Populate Data ---
			if (!dataList.isEmpty()) {
				archivalpopulateEntity1Data(sheet, dataList.get(0), textStyle, numberStyle, numberStyle);
			}

			if (!dataList1.isEmpty()) {
				archivalpopulateEntity2Data(sheet, dataList1.get(0), textStyle, numberStyle, numberStyle);
			}

			if (!dataList2.isEmpty()) {
				archivalpopulateEntity3Data(sheet, dataList2.get(0), textStyle, numberStyle, numberStyle);
			}

			if (!dataList3.isEmpty()) {
				archivalpopulateEntity4Data(sheet, dataList3.get(0), textStyle, numberStyle, numberStyle);
			}

			if (!dataList4.isEmpty()) {
				archivalpopulateEntity5Data(sheet, dataList4.get(0), textStyle, numberStyle, numberStyle);
			}

			// --- Finalize Workbook ---
			workbook.getCreationHelper().createFormulaEvaluator().evaluateAll();
			workbook.write(out);

			logger.info("Service: Excel data successfully written to memory buffer ({} bytes).", out.size());
			return out.toByteArray();

		} catch (Exception e) {
			logger.error("Service: Error occurred while generating Excel file: {}", e.getMessage(), e);
			throw e;
		}
	}

